import React from "react";

export const GifGrid = ({ anime }) => {

    const getGifs = async() => {

        const url = 'https://api.giphy.com/v1/gifs/search?q=Naruto&limit=10&api_key=j6Gcl3UXbQoa0RP4Gw2EMvAVsxSk4EYH'
        const resp = await fetch( url )
        const { data } = await resp.json()


        const gifs = data.map( img => {
            return {
                id:img.id,
                title:img.title,
                url:img.images?.downsized_medium.url
            }
        })

        // console.log(gifs);
    }

    getGifs()

    return (
        <div>
        <h2>{anime}</h2>
        </div>
    );
};
